class inMessage {
    constructor(payload) {
        this.payload = payload;
    }
    getPayload() {
        return this.payload;
    }
    getText() {
	return this.payload.text;
    }
    getChannel() {
	return this.payload.channel;
    }
    getMsgType() {
	if (typeof this.payload.subtype !== 'undefined') {
	   return this.payload.subtype;
	} else { 
	   return this.payload.type;
	} 
    }
}

module.exports = inMessage;
